import FirebaseCoreimport UIKitclass FlipAppDelegate: NSObject, UIApplicationDelegate {    func application(        _ application: UIApplication,        didFinishLaunchingWithOptions launchOptions: [UIApplication            .LaunchOptionsKey: Any]? = nil    ) -> Bool {        FirebaseApp.configure()        let locationHandler = LocationHandler.shared        // If location updates were previously active, restart them after the background launch.        if locationHandler.updatesStarted {            locationHandler.startLocationUpdates()        }        // If a background activity session was previously active, reinstantiate it after the background launch.        if locationHandler.backgroundActivity {            locationHandler.backgroundActivity = true        }        return true    }}